import React from 'react'
import '../style.css'
import Message from './Message'
import PropTypes from 'prop-types'
import lifecycle from 'react-pure-lifecycle';

let messagesEnd = [];
let i;

const methods = {
  componentDidUpdate(props) {
    setTimeout(scrollToBottom,750);
  }
};

const MessageList = (props) => {
    let messages = props.messages;
    scrollToBottom = () => {
      messagesEnd[i].scrollIntoView({ behavior: "smooth"});
    }
    return (
      <div className="message-list">
      {messages.map((message, index) => {
            console.log(message)
            if(message.sender){i = index;}
            return (
              <Message key={index} text={message.text} sender={message.sender} source={message.source} handler={()=>{message.handler()}} ref={(el) => { messagesEnd.push(el); }}/>
            )
        })}
        </div>
      )
};

MessageList.propTypes = {
    messages: PropTypes.array.isRequired
};

export default lifecycle(methods)(MessageList);
