import React,{Component} from 'react'
import '../style.css'
import Message from './Message'
import PropTypes from 'prop-types'
import lifecycle from 'react-pure-lifecycle';

class MessageList extends Component {
    state = {messages:null}
    constructor(){
      super();
      this.messagesEnd = [];
      this.i = 0;
    }
    scrollToBottom = (i,messagesEnd) => {
      messagesEnd[i].scrollIntoView({ behavior: "smooth"});
    }
    static getDerivedStateFromProps(nextProps,prevState){
      if(nextProps.messages !== prevState.messages){
      return {messages: nextProps.messages};
      }
      return null;
    }
    componentDidUpdate() {
        setTimeout(this.scrollToBottom,750);
    }
    render(){
      return(
      <div className="message-list">
      {this.state.messages.map((message, index) => {
            console.log(message)
            if(message.sender){this.i = index;}
            return (
              <Message key={index} text={message.text} sender={message.sender} source={message.source} handler={()=>{message.handler()}} ref={(el) => { this.messagesEnd.push(el); }}/>
            )
        })}
        </div>
      )
    }
};

MessageList.propTypes = {
    messages: PropTypes.array.isRequired
};

export default MessageList;
